        uint256 startTime;
        uint256 stopTime;
        uint256 interval;
        uint256 ratePerInterval;
        uint256 remainingBalance;
        uint256 lastWithdrawTime;
        uint256 createdAt;
        uint256 autoWithdrawInterval;
        bool autoWithdraw;
        bool closed;
        bool isActive;
        CliffData cliffData;
        PermissionData permissionData;
        PauseData pauseData;
    }

    struct CliffData {
        uint256 cliffAmount;
        uint256 cliffTime;
        bool cliffClaimed;
    }

    struct PermissionData {
        Permission pauseable;
        Permission closeable;
        Permission recipientModifiable;
    }

    struct PauseData {
        uint256 pausedAt;
        uint256 totalPausedTime;
        address pausedBy;
        bool isPaused;
    }

    struct GlobalConfig {
        address weth;
        address gateway;
        address feeRecipient;
        address autoWithdrawAccount;
        uint256 autoWithdrawFeeForOnce;
        uint256 tokenFeeRate;
