contracts/ETHStreamInterface.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.0;

import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IWETH} from "./interfaces/IWETH.sol";
import {IStream} from "./interfaces/IStream.sol";
import {StreamStructs} from "./libraries/StreamStructs.sol";

contract ETHStreamInterface is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // Custom Errors
    error InvalidTokenAddress();
    error ArrayLengthMismatch();
    error InvalidStopTime();
    error TransferFailed();
    error UnauthorizedReceiver();
    error FallbackNotAllowed();

    IWETH internal immutable WETH;
    IStream internal immutable STREAM;

    constructor(address _weth, IStream _stream) ReentrancyGuard() Ownable() {
        WETH = IWETH(_weth);    
        STREAM = _stream;
        IWETH(_weth).approve(address(_stream), type(uint256).max);
    }

    function createMultipleStreamsETH(
        StreamStructs.initializeStreamData[] calldata params
    ) external payable nonReentrant {
        (uint256 totalDeposit, uint256 totalAutoWithdrawFee) = _processMultipleStreamCreation(params);
        _refundExcess(totalDeposit + totalAutoWithdrawFee);
    }

    function initializeStreamETH(
        StreamStructs.initializeStreamData calldata params
    ) external payable nonReentrant {
        if (params.tokenAddress != address(0x00)) revert InvalidTokenAddress();

        uint256 autoWithdrawFee = _calculateAutoWithdrawFee(params);

        WETH.deposit{value: params.deposit}();

        StreamStructs.initializeStreamData memory streamParams = _buildStreamParams(params);
        STREAM.initializeStream{value: autoWithdrawFee}(streamParams);

        _handleCliffPayment(params);
        _refundExcess(params.deposit + autoWithdrawFee);
    }

    function prolongMultipleStreamsETH(
        uint256[] calldata streamIds,
        uint256[] calldata newStopTimes
    ) external payable nonReentrant {
        if (streamIds.length != newStopTimes.length) revert ArrayLengthMismatch();

        (uint256 totalDeposit, uint256 totalAutoWithdrawFee) = _processMultipleStreamExtension(streamIds, newStopTimes);
        _refundExcess(totalDeposit + totalAutoWithdrawFee);
    }

    function prolongStreamETH(
        uint256 streamId,
        uint256 newStopTime
    ) external payable nonReentrant {
        StreamStructs.StreamData memory stream = STREAM.getStream(streamId);
        (uint256 newDeposit, uint256 autoWithdrawFee) = _calculateExtensionCosts(stream, newStopTime);

        WETH.deposit{value: newDeposit}();
        STREAM.ExtendFlow{value: autoWithdrawFee}(streamId, newStopTime);

        _refundExcess(newDeposit + autoWithdrawFee);
    }

    function claimFromMultipleStreamsETH(
        uint256[] calldata streamIds
    ) external nonReentrant {
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();

        for (uint256 i; i < streamIds.length; ++i) {
            _processClaim(streamIds[i], feeRate, feeRecipient);
        }
    }

    function claimFromStreamETH(uint256 streamId) external nonReentrant {
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();
        _processClaim(streamId, feeRate, feeRecipient);
    }

    function terminateMultipleStreamsETH(
        uint256[] calldata streamIds
    ) external nonReentrant {
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();

        for (uint256 i; i < streamIds.length; ++i) {
            _processTermination(streamIds[i], feeRate, feeRecipient);
        }
    }

    function terminateStreamETH(uint256 streamId) external nonReentrant {
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();
        _processTermination(streamId, feeRate, feeRecipient);
    }

    function haltMultipleStreamsETH(
        uint256[] calldata streamIds
    ) external nonReentrant {
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();

        for (uint256 i; i < streamIds.length; ++i) {
            _processHalt(streamIds[i], feeRate, feeRecipient);
        }
    }

    function haltStreamETH(uint256 streamId) external nonReentrant {
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();
        _processHalt(streamId, feeRate, feeRecipient);
    }

    // Internal helper functions
    function _processMultipleStreamCreation(
        StreamStructs.initializeStreamData[] calldata params
    ) internal returns (uint256 totalDeposit, uint256 totalAutoWithdrawFee) {
        uint256 autoWithdrawFeeForOnce = STREAM.autoWithdrawFeeForOnce();
        uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
        address feeRecipient = STREAM.feeRecipient();

        for (uint256 i; i < params.length; ++i) {
            StreamStructs.initializeStreamData calldata currentParams = params[i];
            if (currentParams.tokenAddress != address(0x00)) revert InvalidTokenAddress();

            uint256 autoWithdrawFee;
            if (currentParams.autoWithdraw) {
                autoWithdrawFee = autoWithdrawFeeForOnce * 
                    ((currentParams.stopTime - currentParams.startTime) / 
                     currentParams.autoWithdrawInterval + 1);
                totalAutoWithdrawFee += autoWithdrawFee;
            }

            totalDeposit += currentParams.deposit;
            WETH.deposit{value: currentParams.deposit}();

            StreamStructs.initializeStreamData memory streamParams = _buildStreamParams(currentParams);
            STREAM.initializeStream{value: autoWithdrawFee}(streamParams);

            if (currentParams.cliffTime <= block.timestamp && currentParams.cliffAmount > 0) {
                uint256 fee = (currentParams.cliffAmount * feeRate) / 10000;
                WETH.withdraw(currentParams.cliffAmount);
                _transferETH(currentParams.recipient, currentParams.cliffAmount - fee);
                _transferETH(feeRecipient, fee);
            }
        }
    }

    function _processMultipleStreamExtension(
        uint256[] calldata streamIds,
        uint256[] calldata newStopTimes
    ) internal returns (uint256 totalDeposit, uint256 totalAutoWithdrawFee) {
        uint256 autoWithdrawFeeForOnce = STREAM.autoWithdrawFeeForOnce();

        for (uint256 i; i < streamIds.length; ++i) {
            StreamStructs.StreamData memory stream = STREAM.getStream(streamIds[i]);
            uint256 duration = newStopTimes[i] - stream.stopTime;
            uint256 delta = duration / stream.interval;

            if (delta * stream.interval != duration) revert InvalidStopTime();

            uint256 newDeposit = delta * stream.ratePerInterval;
            totalDeposit += newDeposit;

            uint256 autoWithdrawFee;
            if (stream.autoWithdraw) {
                autoWithdrawFee = autoWithdrawFeeForOnce * 
                    (duration / stream.autoWithdrawInterval + 1);
                totalAutoWithdrawFee += autoWithdrawFee;
            }

            WETH.deposit{value: newDeposit}();
            STREAM.ExtendFlow{value: autoWithdrawFee}(streamIds[i], newStopTimes[i]);
        }
    }

    function _calculateExtensionCosts(
        StreamStructs.StreamData memory stream,
        uint256 newStopTime
    ) internal view returns (uint256 newDeposit, uint256 autoWithdrawFee) {
        uint256 duration = newStopTime - stream.stopTime;
        uint256 delta = duration / stream.interval;

        if (delta * stream.interval != duration) revert InvalidStopTime();

        newDeposit = delta * stream.ratePerInterval;

        if (stream.autoWithdraw) {
            uint256 autoWithdrawFeeForOnce = STREAM.autoWithdrawFeeForOnce();
            autoWithdrawFee = autoWithdrawFeeForOnce * 
                (duration / stream.autoWithdrawInterval + 1);
        }
    }

    function _processClaim(uint256 streamId, uint256 feeRate, address feeRecipient) internal {
        StreamStructs.StreamData memory stream = STREAM.getStream(streamId);
        uint256 balance = STREAM.balanceOf(streamId, stream.recipient);

        STREAM.WithdrawFromFlow(streamId);
        if (balance > 0) {
            uint256 fee = (balance * feeRate) / 10000;
            WETH.withdraw(balance);
            _transferETH(stream.recipient, balance - fee);
            _transferETH(feeRecipient, fee);
        }
    }

    function _processTermination(uint256 streamId, uint256 feeRate, address feeRecipient) internal {
        StreamStructs.StreamData memory stream = STREAM.getStream(streamId);
        uint256 senderBalance = STREAM.balanceOf(streamId, stream.sender);
        uint256 recipientBalance = STREAM.balanceOf(streamId, stream.recipient);

        STREAM.closeStream(streamId);
        WETH.withdraw(senderBalance + recipientBalance);

        if (senderBalance > 0) {
            _transferETH(stream.sender, senderBalance);
        }
        if (recipientBalance > 0) {
            uint256 fee = (recipientBalance * feeRate) / 10000;
            _transferETH(stream.recipient, recipientBalance - fee);
            _transferETH(feeRecipient, fee);
        }
    }

    function _processHalt(uint256 streamId, uint256 feeRate, address feeRecipient) internal {
        StreamStructs.StreamData memory stream = STREAM.getStream(streamId);
        uint256 balance = STREAM.balanceOf(streamId, stream.recipient);

        STREAM.pauseStream(streamId);
        if (balance > 0) {
            uint256 fee = (balance * feeRate) / 10000;
            WETH.withdraw(balance);
            _transferETH(stream.recipient, balance - fee);
            _transferETH(feeRecipient, fee);
        }
    }

    function _calculateAutoWithdrawFee(
        StreamStructs.initializeStreamData calldata params
    ) internal view returns (uint256) {
        if (!params.autoWithdraw) return 0;

        uint256 autoWithdrawFeeForOnce = STREAM.autoWithdrawFeeForOnce();
        return autoWithdrawFeeForOnce * 
            ((params.stopTime - params.startTime) / params.autoWithdrawInterval + 1);
    }

    function _buildStreamParams(
        StreamStructs.initializeStreamData calldata params
    ) internal view returns (StreamStructs.initializeStreamData memory) {
        return StreamStructs.initializeStreamData({
            sender: params.sender,
            recipient: params.recipient,
            deposit: params.deposit,
            tokenAddress: address(WETH),
            startTime: params.startTime,
            stopTime: params.stopTime,
            interval: params.interval,
            cliffAmount: params.cliffAmount,
            cliffTime: params.cliffTime,
            autoWithdrawInterval: params.autoWithdrawInterval,
            autoWithdraw: params.autoWithdraw,
            pauseable: params.pauseable,
            closeable: params.closeable,
            recipientModifiable: params.recipientModifiable
        });
    }

    function _handleCliffPayment(StreamStructs.initializeStreamData calldata params) internal {
        if (params.cliffTime <= block.timestamp && params.cliffAmount > 0) {
            uint256 feeRate = STREAM.tokenFeeRate(address(WETH));
            address feeRecipient = STREAM.feeRecipient();
            uint256 fee = (params.cliffAmount * feeRate) / 10000;
            WETH.withdraw(params.cliffAmount);
            _transferETH(params.recipient, params.cliffAmount - fee);
            _transferETH(feeRecipient, fee);
        }
    }

    function _refundExcess(uint256 totalUsed) internal {
        if (msg.value > totalUsed) {
            _transferETH(msg.sender, msg.value - totalUsed);
        }
    }

    function _transferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        if (!success) revert TransferFailed();
    }

    // Emergency functions
    function emergencyTokenTransfer(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner {
        IERC20(token).safeTransfer(to, amount);
    }

    function emergencyEtherTransfer(
        address to,
        uint256 amount
    ) external onlyOwner {
        _transferETH(to, amount);
    }

    // View functions
    function getWETHAddress() external view returns (address) {
        return address(WETH);
    }

    receive() external payable {
        if (msg.sender != address(WETH) && msg.sender != address(STREAM)) {
            revert UnauthorizedReceiver();
        }
    }

    fallback() external payable {
        revert FallbackNotAllowed();
    }
}

