// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.0;

library Struct {

    enum Capability {
        None,       //0
        Sender,
        Recipient,
        Both
    }

    struct Stream {
        address onBehalfOf;
        address sender;
        address recipient;
        uint256 deposit;
        address tokenAddress;
        uint256 startTime;
        uint256 stopTime;
        uint256 interval;
        uint256 ratePerInterval;
        uint256 remainingBalance;
        uint256 lastWithdrawTime;
        uint256 createAt;
        uint256 autoWithdrawInterval;
        bool autoWithdraw;
        bool closed;
        bool isEntity;
        CliffInfo cliffInfo;
        FeatureInfo featureInfo;
        PauseInfo pauseInfo;
    }

    struct CliffInfo {
        uint256 cliffAmount;
        uint256 cliffTime;
        bool cliffDone;
    }

    struct FeatureInfo {
        Capability pauseable;
        Capability closeable;
        Capability recipientModifiable;
    }

    struct PauseInfo {
        uint256 pauseAt;
        uint256 accPauseTime;
        address pauseBy;
        bool isPaused;
    }

    struct GlobalParams {
        address weth;
        address gateway;
        address feeRecipient;
        address autoWithdrawAccount;
        uint256 autoWithdrawFeeForOnce;
        uint256 tokenFeeRate;
    }

    struct initializeStreamParams {
        address sender;
        address recipient;
        uint256 deposit;
        address tokenAddress;
        uint256 startTime;
        uint256 stopTime;
        uint256 interval;
        uint256 cliffAmount;
        uint256 cliffTime;
        uint256 autoWithdrawInterval;
        bool autoWithdraw;
        Capability pauseable;
        Capability closeable;
        Capability recipientModifiable;
    }
}

contracts/libraries/WithdrawLogic.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Struct} from "./Struct.sol";

library WithdrawLogic {
    using SafeERC20 for IERC20;

    /**
     * @notice Emits when the recipient of a stream withdraws a portion or all their pro rata share of the stream.
     */
    event WithdrawFromFlow(uint256 indexed streamId, address indexed operator, uint256 recipientBalance);

    function withdraw(
        uint256 streamId,
        uint256 delta,
        uint256 balance,
        Struct.GlobalParams memory globalParams,
        Struct.Stream storage stream
    )
        internal
    {
        require(stream.pauseInfo.isPaused == false, "stream is paused");
        require(stream.closed == false, "stream is closed");

        stream.remainingBalance = stream.remainingBalance - balance;
        if (delta > 0) {
            stream.lastWithdrawTime += stream.interval * delta + stream.pauseInfo.accPauseTime;
            stream.pauseInfo.accPauseTime = 0;
        }



        if (globalParams.weth == stream.tokenAddress && msg.sender == stream.onBehalfOf) {
            IERC20(stream.tokenAddress).safeTransfer(stream.onBehalfOf, balance);
        } else {
            uint256 fee = balance * globalParams.tokenFeeRate / 10000;
            IERC20(stream.tokenAddress).safeTransfer(globalParams.feeRecipient, fee);
            IERC20(stream.tokenAddress).safeTransfer(stream.recipient, balance - fee);
        }

        /* cliff */
        if (stream.cliffInfo.cliffDone == false && stream.cliffInfo.cliffTime <= block.timestamp) {
            // uint256 cliffAmountFee = stream.cliffAmount * _tokenFeeRate[stream.tokenAddress] / 10000;
            // IERC20(stream.tokenAddress).safeTransfer(_feeRecipient, cliffAmountFee);
            // IERC20(stream.tokenAddress).safeTransfer(stream.recipient, stream.cliffAmount - cliffAmountFee);
            stream.cliffInfo.cliffDone = true;
        }

        emit WithdrawFromFlow(streamId, msg.sender, balance);
    }
}

